oration: none;\n}\n\n.button:hover, .button:visited:hover {\n	text-decoration: none;\n	color: #fff;\n	background-color: #006cfc;\n}\n\n/* header */\n.site-title, .site-description {\n	display: none;\n}\n\n/* Layout */\n.body {\n	background-color: #fff;\n}\n\n.site {\n	background: none;\n	margin: 0 0 50px;\n	max-width: 100%;\n	min-width: 250px;\n	padding: 0 30px;\n}\n\n.site-header {\n	padding: 0;\n}\n\n#main {\n	background-color: #fff;\n	border-radius: 5px;\n	box-shadow: 0 1px 5px #ccc;\n	margin: 315px auto 0;\n	max-width: 1030px;\n	overflow: visible;\n	position: relative;\n	padding-top: 15px;\n}\n\n#primary {\n	padding: 0 4%;\n}\n\nbody.single-attachment .site-content,\nbody.full-width .site-content {\n	width: auto;\n}\n\n#colophon {\n	border: none;\n	margin: 0 auto;\n	max-width: 1030px;\n	text-align: center;\n}\n\n/* Primary Menu */\n.site-header {\n	background-color: none;\n	padding: 0;\n}\n\n.main-navigation {\n	margin: 0 auto;\n	text-align: center;\n	max-width: 1030px;\n}\n\n.main-navigation ul.nav-menu,\n.main-navigation div.nav-menu > ul {\n	border: none;\n	text-align: center !important;\n}\n\n.main-navigation li {\n	margin: 0;\n	font-family: \'Montserrat\', sans-serif;\n	font-weight: 400;\n}\n\n.main-navigation li a {\n	color: #fff;\n	padding: 0 14px;\n	-webkit-transition: color .3s ease-in;\n	-moz-transition: color .3s ease-in;\n	-o-transition: color .3s ease-in;\n	transition: color .3s ease-in;\n	text-shadow: 1px 1px 1px #444;\n}\n\n.main-navigation li a:hover {\n	color: #dce2ff;\n}\n\n.main-navigation .current-menu-item > a,\n.main-navigation .current-menu-ancestor > a,\n.main-navigation .current_page_item > a,\n.main-navigation .current_page_ancestor > a {\n	color: #dce2ff;\n}\n\n.main-navigation ul.nav-menu.toggled-on,\n.menu-toggle,\n.menu-toggle:active,\n.menu-toggle.toggled-on,\nbutton:active {\n	background: none repeat scroll 0 0 transparent;\n	border: medium none;\n	border-radius: 0 0 0 0;\n	box-shadow: none;\n	color: #b2b2b0;\n	font-size: 12px;\n	line-height: 32px;\n	padding: 0;\n	text-transform: uppercase;\n	width: 100%;\n	-webkit-transition: color .2s ease-in;\n	-moz-transition: color .2s ease-in;\n	-o-transition: color .2s ease-in;\n	transition: color .2s ease-in;\n}\n\n.main-navigation ul.nav-menu.toggled-on:hover,\n.menu-toggle:hover {\n	background: none repeat scroll 0 0 transparent;\n	border: medium none;\n	border-radius: 0 0 0 0;\n	color: #FFFFFF;\n}\n\n.main-navigation li ul li a {\n	color: #333;\n	text-shadow: none;\n}\n\n.main-navigation li.no-pointer a {\n	font-size: 13px;\n	background-color: #333;\n	line-height: 44px;\n	margin-top: 11px;\n	cursor: default;\n	text-shadow: none;\n	padding: 0 15px;\n	margin-right: 20px;\n}\n\n.main-navigation li.no-pointer a:hover {\n	cursor: default;\n	color: #fff;\n}\n\n.main-navigation li.no-pointer a span {\n	color: #78a3e0;\n}\n\n.main-navigation li ul li.current_page_item > a {\n	color: #78a3e0;\n}\n\n/* Content */\n.single-author .entry-meta .by-author {\n	display: inline;\n}\n\n.entry-content li {\n	margin-bottom: 10px;\n}\n\n.single-wcb_sponsor img.wp-post-image, body.page-id-451 .entry-content img, .entry-content img {\n	box-shadow: none;\n}\n\n.single-wcb_sponsor .entry-meta, .single-wcb_sponsor .nav-single {\n	display: none;\n}\n\nbody.template-front-page .entry-content h1 {\n	background: transparent url(\'//2014.seattle.wordcamp.org/files/2014/01/blue-dot.png\') no-repeat center left;\n	padding: 16px 0 16px 21px;\n}\n\n/* Sessions Page */\n.wcorg-session {\n	padding-bottom: 20px;\n	margin-bottom: 20px;\n	border-bottom: 3px double #999;\n}\n\n.wcorg-session h2 {\n	margin-bottom: 5px;\n	font-size: 24px;\n}\n\n.wcorg-session .wcpt-session-meta {\n	font-size: 12px;\n	color: #999;\n	margin-bottom: 5px;\n}\n\n/* Frontpage */\nbody.template-front-page #page #main {\n	background: #fff url(\'\\\'\') repeat-x top left;\n	padding: 190px 0 0;\n}\n\nbody.template-front-page #page .site-content {\n	float: left;\n	width: 58%;\n}\n\nbody.template-front-page #page .widget-area {\n	border: none;\n	clear: none;\n	float: right;\n	padding: 0 2% 0 0;\n	width: 32%;\n}\n\nbody.template-front-page #page .first.front-widgets {\n	float: none;\n	height: 240px;\n	left: 0;\n	overflow: hidden;\n	position: absolute;\n	top: -63px;\n	width: 100%;\n}\n\nbody.template-front-page #page .first.front-widgets .widget {\n	background: none;\n	border-radius: 0;\n	box-shadow: none;\n	clear: none;\n	float: left;\n	padding: 100px 0 0;\n	text-align: center;\n	width: 33.33%;\n}\n\nbody.template-front-page #page .widget-area {\n	padding-top: 40px;\n}\n\n.header-widget-inner {\n	border-right: solid 1px #cecece;\n	padding: 15px 35px 5px;\n}\n\n#text-4 .header-widget-inner {\n	border-right: none;\n}\n\n.first.front-widgets .widget h4 {\n	font-size: 24px;\n	font-weight: 400;\n	line-height: 24px;\n	margin: 0 0 10px;\n	text-transform: none;\n}\n\n#page .first.front-widgets .widget h4 a {\n	color: #4a4946;\n	text-decoration: none;\n	-webkit-transition: color .2s ease-in;\n	-moz-transition: color .2s ease-in;\n	-o-transition: color .2s ease-in;\n	transition: color .2s ease-in;\n}\n\n#page .first.front-widgets .widget h4 a:hover {\n	color: #1b65c7;\n	text-decoration: none;\n}\n\n#page .first.front-widgets .widget p {\n	font-size: 14px;\n	font-weight: 200;\n	line-height: 20px;\n}\n\nbody.template-front-page #page .second.front-widgets {\n	float: right;\n	width: 100%;\n}\n\n.template-front-page .widget-area .widget li {\n	list-style-type: none;\n}\n\n.template-front-page .widget-area .widget h3 {\n	margin-bottom: 0;\n}\n\n/* Typography */\nbody.custom-font-enabled {\n	font-family: \"Raleway\", \"Segoe UI\", \"Bitstream Vera Sans\", \"DejaVu Sans\", \"Bitstream Vera Sans\", \"Trebuchet MS\", Verdana, \"Verdana Ref\", sans-serif;\n}\n\n#content {\n	font-size: 15px;\n}\n\n#page h1 {\n	font-family: Montserrat;\n	font-size: 24px;\n	font-weight: 400;\n	line-height: 24px;\n	text-transform: uppercase;\n}\n\nh2 {\n	font-family: Montserrat;\n	font-weight: 400;\n}\n\nh3 {\n	font-fam