able td {\n	padding: 13px 0;\n}\n\n.schedule-table tbody td:nth-child(2n+1) {\n	background-color: #f5f5f5;\n}\n\n.schedule-table td.t-time {\n	color: #333;\n	text-align: left;\n	background-color: transparent;\n}\n\n.schedule-table tbody td.t-time {\n	background-color: transparent;\n}\n\n.table-legend {\n	width: auto !important;\n}\n\n.t-beg {\n	background-color: lightblue;\n}\n\n.t-int {\n	background-color: white;\n}\n\n.t-adv {\n	background-color: lightcoral;\n}\n\n.t-row {\n	text-align: center;\n}\n\n.wcpt-session-title {\n	font-weight: bold;\n}\n\n.wcpt-session-speakers:before {\n	display: inline;\n	content: \'- \';\n}\n\ntr.global-session td.wcpt-session-type-session,\ntr.global-session td.wcpt-session-type-custom,\ntd.wcb-session-zero-hour-networking {\n	text-align: center;\n}\n\ntr.wcpt-time-1120am td,\ntr.wcpt-time-120pm td,\ntr.wcpt-time-230pm td,\ntr.wcpt-time-340pm td {\n	border-top: none;\n}\n\n/* Footer */\n.site-info a {\n	text-decoration: none;\n}\n\n/* Sponsorship Page */\n.sponsor-box-left {\n	width: 46%;\n	margin-right: 5%;\n	float: left;\n}\n\n.sponsor-box-right {\n	width: 46%;\n	float: left;\n}\n\n.sponsor-banner {\n	background-color: #eee;\n	margin: 20px auto 40px;\n	padding: 1px;\n	width: 97%;\n	-webkit-border-radius: 8px;\n	-moz-border-radius: 8px;\n	border-radius: 8px;\n	text-align: center;\n}\n\n/* Sidebar */\nh4.sponsor-level-title {\n	font-size: 18px;\n	margin: 0 0 15px;\n	border-bottom: 3px double #ccc;\n	padding-bottom: 5px;\n}\n\na.sponsor-logo img.wp-post-image {\n	box-shadow: none;\n	margin-bottom: 10px;\n}\n\ndiv.sponsor-level {\n	margin-bottom: 20px;\n}\n\n.widget .in-kind {\n	display: none;\n}\n\n/* Camp Tix */\n#tix-attendees .tix-columns-4 li {\n	width: 29% !important;\n}\n\n.tix-column-quantity {\n	padding-left: 5px;\n}\n\n/* Tagregator */\nbody.page-id-12.full-width .site-content {\n	width: 100%;\n}\n\n/* WordPress.tv */\n.wptv-video-item {\n	width: 43% !important;\n}\n\n/* =Media queries\n---------------------------------------