x-shadow: none;\n	clear: none;\n	float: left;\n	padding: 100px 0 0;\n	text-align: center;\n	width: 33.33%;\n}\n\nbody.template-front-page #page .widget-area {\n	padding-top: 40px;\n}\n\n.header-widget-inner {\n	border-right: solid 1px #cecece;\n	padding: 15px 35px 5px;\n}\n\n#text-4 .header-widget-inner {\n	border-right: none;\n}\n\n.first.front-widgets .widget h4 {\n	font-size: 24px;\n	font-weight: 400;\n	line-height: 24px;\n	margin: 0 0 10px;\n	text-transform: none;\n}\n\n#page .first.front-widgets .widget h4 a {\n	color: #4a4946;\n	text-decoration: none;\n	-webkit-transition: color .2s ease-in;\n	-moz-transition: color .2s ease-in;\n	-o-transition: color .2s ease-in;\n	transition: color .2s ease-in;\n}\n\n#page .first.front-widgets .widget h4 a:hover {\n	color: #1b65c7;\n	text-decoration: none;\n}\n\n#page .first.front-widgets .widget p {\n	font-size: 14px;\n	font-weight: 200;\n	line-height: 20px;\n}\n\nbody.template-front-page #page .second.front-widgets {\n	float: right;\n	width: 100%;\n}\n\n.template-front-page .widget-area .widget li {\n	list-style-type: none;\n}\n\n.template-front-page .widget-area .widget h3 {\n	margin-bottom: 0;\n}\n\n/* Typography */\nbody.custom-font-enabled {\n	font-family: \"Raleway\", \"Segoe UI\", \"Bitstream Vera Sans\", \"DejaVu Sans\", \"Bitstream Vera Sans\", \"Trebuchet MS\", Verdana, \"Verdana Ref\", sans-serif;\n}\n\n#content {\n	font-size: 15px;\n}\n\n#page h1 {\n	font-family: Montserrat;\n	font-size: 24px;\n	font-weight: 400;\n	line-height: 24px;\n	text-transform: uppercase;\n}\n\nh2 {\n	font-family: Montserrat;\n	font-weight: 400;\n}\n\nh3 {\n	font-family: Montserrat;\n	font-weight: 400;\n}\n\nh4 {\n	font-family: Montserrat;\n	font-weight: 400;\n}\n\n/* Content */\nbody.page .entry-header h1 {\n	background: transparent url(\'https://2014.seattle.wordcamp.org/files/2014/01/blue-dot.png\') no-repeat center left;\n	padding: 16px 0 16px 21px;\n}\n\n/* Widgets */\n#page .widget-area {\n	padding-top: 25px;\n}\n\n#page .widget {\n	background: #f5f5f5 url(\'http://2013.seattle.wordcamp.org/files/2013/03/widget-bg.jpg\') repeat top left;\n	border-radius: 5px;\n	box-shadow: 0 1px 3px #d5d5d5;\n	margin: 0 0 50px;\n	padding: 10px 30px;\n}\n\n.widget-title {\n	background: transparent url(\'https://2014.seattle.wordcamp.org/files/2014/01/blue-dot.png\') no-repeat top center;\n	font-family: Montserrat;\n	font-size: 24px;\n	font-weight: 400;\n	line-height: 24px;\n	margin: -36px 0 0;\n	padding: 60px 0 0;\n	text-align: center;\n	text-transform: none;\n}\n\n.widget h5 {\n	background: transparent url(\'http://2013.minneapolis.wordcamp.org/files/2013/03/widget-h5-bg.jpg\') repeat-x center left;\n	color: #49c9f2;\n	font-size: 13px;\n	font-weight: 700;\n	line-height: 13px;\n	margin: 50px 0 30px;\n	padding: 0 5px;\n	text-align: center;\n	text-transform: none;\n	text-shadow: 1px 0 0 #ffffff;\n}\n\n#page .widget_text img {\n	border-radius: 0;\n	box-shadow: none;\n	float: none;\n	margin: 0;\n}\n\n#page .widget_text,\n#page .widget_text p {\n	font-size: 13px;\n	line-height: 22px;\n}\n\n#page .widget_text a {\n	color: #006cfc;\n}\n\n#page .widget_text a:hover {\n	color: #006cfc;\n}\n\n#page .widget_text #buddycamp-header {\n	display: block;\n	margin: -30px auto 20px;\n}\n\n.sponsor-logo {\n	text-align: center;\n}\n\n#page .widget_text .widget-link {\n	font-weight: bold;\n	margin: 0 0 5px;\n	text-align: center;\n}\n\n.widget_twitter_timeline iframe {\n	width: 100% !important;\n}\n\n/* Schedule Table */\n.schedule-table th {\n	text-align: center;\n	font-size: 1em;\n	color: #333;\n}\n\n.schedule-table tbody td {\n	text-align: center;\n}\n\n.entry-content .schedule-table td {\n	padding: 13px 0;\n}\n\n.schedule-table tbody td:nth-child(2n+1) {\n	background-color: #f5f5f5;\n}\n\n.schedule-table td.t-time {\n	color: #333;\n	text-align: left;\n	background-color: transparent;\n}\n\n.schedule-table tbody td.t-time {\n	background-color: transparent;\n}\n\n.table-legend {\n	width: auto !important;\n}\n\n.t-beg {\n	background-color: lightblue;\n}\n\n.t-int {\n	background-color: white;\n}\n\n.t-adv {\n	background-color: lightcoral;\n}\n\n.t-row {\n	text-align: center;\n}\n\n.wcpt-session-title {\n	font-weight: bold;\n}\n\n.wcpt-session-speakers:before {\n	display: inline;\n	content: \'- \';\n}\n\ntr.global-session td.wcpt-session-type-session,\ntr.global-session td.wcpt-session-type-custom,\ntd.wcb-session-zero-hour-networking {\n	text-align: center;\n}\n\ntr.wcpt-time-1120am td,\ntr.wcpt-time-120pm td,\ntr.wcpt-time-230pm td,\ntr.wcpt-time-340pm td {\n	border-top: none;\n}\n\n/* Footer */\n.site-info a {\n	text-decoration: none;\n}\n\n/* Sponsorship Page */\n.sponsor-box-left {\n	width: 46%;\n	margin-right: 5%;\n	float: left;\n}\n\n.sponsor-box-right {\n	width: 46%;\n	float: left;\n}\n\n.sponsor-banner {\n	background-color: #eee;\n	margin: 20px auto 40px;\n	padding: 1px;\n	width: 97%;\n	-webkit-border-radius: 8px;\n	-moz-border-radius: 8px;\n	border-radius: 8px;\n	text-align: center;\n}\n\n/* Sidebar */\nh4.sponsor-level-title {\n	font-size: 18px;\n	margin: 0 0 15px;\n	border-bottom: 3px double #ccc;\n	padding-bottom: 5px;\n}\n\na.sponsor-logo img.wp-post-image {\n	box-shadow: none;\n	margin-bottom: 10px;\n}\n\ndiv.sponsor-level {\n	margin-bottom: 20px;\n}\n\n.widget .in-kind {\n	display: none;\n}\n\n/* Camp Tix */\n#tix-attendees .tix-columns-4 li {\n	width: 29% !important;\n}\n\n.tix-column-quantity {\n	padding-left: 5px;\n}\n\n/* Tagregator */\nbody.page-id-12.full-width .site-content {\n	width: 100%;\n}\n\n/* WordPress.tv */\n.wptv-video-item {\n	width: 43% !important;\n}\n\n/* =Media queries\n-------------------------------------------------------------- */\n/* Max width of 600 pixels. */\n@media screen and (max-width: 600px) {\n	#primary {\n		padding: 0 6%;\n	}\n	\n	#main, body.home #main {\n		margin-top: 50px;\n	}\n	\n	.main-navigation li a {\n		display: block;\n		text-decoration: none;\n	}\n	\n	.main-navigation li ul li a {\n		color: #fff;\n	}\n	\n	body.template-front-page #page .site-content {\n		float: none;\n		width: auto;\n	}\n	\n	body.template-front-page #page .widget-area {\n		float: none;\n		padding: 0;\n		width: auto;\n	}\n	\n	.main-navigation ul.nav-menu, .main-navigation div.nav-menu > ul {\n		text-align: center !important;\n	}\n	\n	.site-header {\n		background-color: #323232;\n	}\n	\n	.sponsor-box-left, .sponsor-box-right {\n		width: 95%;\n		margin: 0 auto 40px;\n		float: none;\n		clear: both;\n	}\n	\n	#tix-attendees .tix-columns-4 li {\n		width: 98% !important;\n		clear: both;\n	}\n	\n	#tix-attendees li {\n		float: none;\n		height: auto;\n		margin-bottom: 20px;\n		width: 98% !important;\n	}\n	\n	#tix-attendees li:after {\n		display: table;\n		content: \'\';\n		clear: both;\n	}\n	\n	.main-navigation li.no-pointer a {\n		background-color: #444;\n		line-height: 30px;\n	}\n	\n	.main-navigation li.no-pointer a span {\n		display: block;\n	}\n}\n\n/* Max width of 900 pixels. */\n@media screen and (max-width: 900px) {\n	body {\n		background: #fafafa url(\'https://2014.seattle.wordcamp.org/files/2014/05/wordcamp-seattle-mobile.jpg\') no-repeat top center;\n	}\n	\n	.site {\n		margin: 0;\n		max-width: 100%;\n		overflow: hidden;\n	}\n	\n	.menu-primary-menu-container {\n		background: none;\n		padding: 0;\n	}\n	\n	.main-navigation li a {\n		font-size: 12px;\n		line-height: 32px;\n	}\n	\n	.main-navigation li.no-pointer {\n		display: block;\n	}\n	\n	.main-navigation li.no-pointer a {\n		margin-right: 0;\n		margin-bottom: 12px;\n	}\n	\n	body.template-front-page #page #main {\n		background: none;\n		background-color: #fff;\n		padding: 0;\n	}\n	\n	.first.front-widgets {\n		display: none;\n	}\n	\n	.wptv-video-item {\n		width: 100% !important;\n		clear: both !important;\n		float: none !important;\n		margin-bottom: 50px !important;\n	}\n}\n\n/* Minimum width of 900 pixels. */\n@media screen and (min-width: 900px) {\n	body {\n		background: #fafafa url(\'https://2014.seattle.wordcamp.org/files/2014/05/wordcamp-seattle-desktop.jpg\') no-repeat top center;\n	}\n	\n	body .site {\n		box-shadow: none;\n		margin: 0;\n	}\n	\n	#main {\n		margin: 315px auto 0;\n	}\n	\n	body.home #main {\n		margin: 435px auto 0;\n	}\n	\n	.menu-primary-menu-container {\n		background: transparent url(\'http://2013.minneapolis.wordcamp.org/files/2013/02/menu-wordpress-logo.jpg\') no-repeat top left;\n		padding: 0 0 0 60px;\n	}\n	\n	.main-navigation li a {\n		font-size: 16px;\n		line-height: 60px;\n	}\n}\n\n/* Minimum width of 1100 pixels. */\n@media screen and (min-width: 1100px) {\n	body .site {\n		padding: 0;\n	}\n}\n\n.wcorg-sponsors h2 {\n	font-size: 1.8rem;\n}','safecss','Twenty Twelve','inherit','closed','closed','','808703-revision-v1','','','2016-06-06 18:58:38','2016-06-06 18:58:38','',808703,'http://2014.seattle.wordcamp.dev/2016/06/06/808703-revision-v1/',0,'revision','',0);
